parameters.functionCode:
const alert = $input.first().json;

return [
  {
    json: {
      position: alert.position || alert.symbol || 'UNKNOWN',
      pnl_percent: alert.pnl_percent || 0,
      pnl_value: alert.pnl_value || 0,
      health_score: alert.health_score || 50,
      reason: alert.reason || 'Unknown',
      action: alert.action || 'monitor',
      timestamp: new Date().toISOString()
    }
  }
];

const alert = $input.first().json;

// Determine alert severity
let severity = 'info';
let emoji = '‚ÑπÔ∏è';

if (alert.pnl_percent <= -10) {
  severity = 'critical';
  emoji = 'üö®üö®üö®';
} else if (alert.pnl_percent <= -5) {
  severity = 'error';
  emoji = 'üî¥';
} else if (alert.pnl_percent <= -2) {
  severity = 'warning';
  emoji = '‚ö†Ô∏è';
} else if (alert.health_score < 40) {
  severity = 'warning';
  emoji = '‚ö†Ô∏è';
}

// Format message
let actionText = '';
if (alert.action === 'close') actionText = '\n\n*Action Required:* Close position immediately!';
if (alert.action === 'reduce') actionText = '\n\n*Action Required:* Reduce position size!';
if (alert.action === 'watch') actionText = '\n\n*Action:* Monitor closely';

const message = `${emoji} *GOBOT Risk Alert* (${severity.toUpperCase()})

*Position:* ${alert.position}
*PnL:* ${alert.pnl_percent > 0 ? '+' : ''}${alert.pnl_percent.toFixed(2)}% ($${alert.pnl_value.toFixed(2)})
*Health Score:* ${alert.health_score}/100
*Reason:* ${alert.reason}${actionText}
*Time:* ${alert.timestamp}

_GOBOT Risk Management_`;

return [
  {
    json: {
      chat_id: process.env.TELEGRAM_CHAT_ID,
      text: message,
      parse_mode: 'Markdown'
    }
  }
];

